import react, {useEffect,useState } from "react";

import { ReactDOM } from "react-dom";
var stat="true"
export function Gameflow(){
  var [gameflow,game_over]=useState(true);
  game_over(stat);
  return(<gameflow/>);
}

function GameStatus() {
  
  
  
    useEffect(() => { 
    
  
  
      const checkCollision = () => {
        const array = document.getElementsByClassName('redcar');
        const mx = document.getElementById('main').getBoundingClientRect().x;
        const my = document.getElementById('main').getBoundingClientRect().y;
        for (let i = 0; i < array.length; i++) {
  
          const x = array[i].getBoundingClientRect().x;
          const y = array[i].getBoundingClientRect().y;
          if (Math.abs(mx - x) <= 30 && Math.abs(my - y) <= 20) {
            // alert('Shapes are touching!');
            // var sc=document.getElementById('score');
            
            // sc.innerText=0;
            stat=false
            console.log("GAME OVER!")
  
            
            return(<div style={{padding:"100px",
          backgroundColor:"green"}}> GAME OVER!</div>)
        
            // Stop checking if a collision is detected
          }
        }
      };
  
      const interval = setInterval(checkCollision, 60);
  
      return () => clearInterval(interval);
    }, []);
  
    return (
      <div>
        {/* Assuming you have elements with class 'redcar' and an element with id 'main' */}
      </div>
    );
  }
export default GameStatus;
