import { useState } from "react";
import { ReactDOM } from "react-dom";


function Main_car(){
    var [left_right,lr_value]=useState(30) ;
    function Turn_right(){
        if (left_right<62){
            lr_value(left_right+3)
            }
                }
          
    function Turn_left(){
        if (left_right>2){ 
            lr_value(left_right-3)
            }
                  }
      
                  const handleKeyDown = (event) => {
                    if (event.key === 'ArrowLeft') {
Turn_left()                      // Perform your action for left arrow key
                    } else if (event.key === 'ArrowRight') {
Turn_right()                      // Perform your action for right arrow key
                    }
                  };
                  document.addEventListener('keydown', handleKeyDown);

    return((  

            <div>          
              <nav  className='main_car' id="main" style={{ 
                position :"relation" ,
                zIndex:"20",
                marginLeft: left_right+"vw"}}>
              </nav>
    
              <div >
                 <div className='bottom'>
                    <button className='leftbutton' onClick={Turn_left} 
                    
                    ></button>
                    <button className='rightbutton'
                    onClick={Turn_right}
                     ></button>
                  </div>
              </div>
            </div>   
                    ))}

export default Main_car;
